class randomQuoteMachine extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      quote: quotes[0][0],
      author: quotes[0][1],
      color: "rgb(255, 170, 0)"

    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    let ind= Math.floor(Math.random() * quotes.length);

      this.setState({
        quote: quotes[ind][0],
        author: quotes[ind][1]
      });


  }

  render(){

    return(

    <div>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" />
      <div id="quoteBox">
        <div id="box1">
        <h1 id="quote"><span class="fa fa-quote-left" id = "quote-icon"></span> {this.state.quote}</h1>
        <p id="author">- {this.state.author}</p>
        </div>
        <div id="box2">
          <button class = "btn" title="post on facebook" ><a href="https://www.facebook.com" target="_blank" class="links"><i class="fa fa-facebook"></i></a></button>
          <button class = "btn" title="post on twitter"><a href="https://twitter.com/freecodecamp" class="links" target="_blank"><i class="fa fa-twitter"></i></a></button>
          <button id = "btn3" onClick = {this.handleClick} title="generate new quote">New quote</button>
        </div>
      </div>
    </div>)
  }
}


export default randomQuoteMachine;
