// import logo from './logo.svg';
import './App.css';
import React from 'react';
import { ReactDOM } from 'react';

const quotes = [["Don’t settle for what life gives you; make life better and build something.", "Ashton Kutcher"],
["Live for each second without hesitation.", "Elton John"],
["Life is a succession of lessons which must be lived to be understood.","Helen Keller"],
["Keep calm and carry on.","Winston Churchill"],
["Keep smiling, because life is a beautiful thing and there’s so much to smile about.","Marilyn Monroe"],
["You have brains in your head. You have feet in your shoes. You can steer yourself any direction you choose.", "Dr. Seuss"],
["The greatest pleasure of life is love.","Euripides"],
["The two most important days in your life are the day you are born and the day you find out why.","Mark Twain"],
["Too many of us are not living our dreams because we are living our fears."," Les Brown"],
["If you live long enough, you'll make mistakes. But if you learn from them, you'll be a better person.", "Bill Clinton"],
["Every moment is a fresh beginning.","T.S. Eliot"],
["If you can do what you do best and be happy, you're further along in life than most people.","Leonardo DiCaprio"],
["It is our choices that show what we truly are, far more than our abilities.","J. K. Rowling"],
["The best way to predict your future is to create it.", "Abraham Lincoln"],
["Sometimes you can't see yourself clearly until you see yourself through the eyes of others.","Ellen DeGeneres"],
["All life is an experiment. The more experiments you make, the better.","Ralph Waldo Emerson"],
["Curiosity about life in all of its aspects, I think, is still the secret of great creative people.","Leo Burnett"]];



class randomQuoteMachine extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      quote: quotes[0][0],
      author: quotes[0][1],
      color: "rgb(255, 170, 0)"

    }

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    let ind= Math.floor(Math.random() * quotes.length);

      this.setState({
        quote: quotes[ind][0],
        author: quotes[ind][1]
      });

    let r = document.querySelector(":root");
    r.style.setAttribute("--page-color", "green");
  }

  render(){

    return(

    <div>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" crossorigin="anonymous" />
      <div id="quoteBox">
        <div id="box1">
        <h1 id="quote"><span class="fa fa-quote-left" id = "quote-icon"></span> {this.state.quote}</h1>
        <p id="author">- {this.state.author}</p>
        </div>
        <div id="box2">
          <button class = "btn" title="post on facebook" ><a href="https://www.facebook.com" target="_blank" style={"ba"}><i class="fa fa-facebook"></i></a></button>
          <button class = "btn"><i class="fa fa-twitter"></i></button>
          <button id = "btn3" onClick = {this.handleClick}>New quote</button>
        </div>
      </div>
    </div>)
  }
}


export default randomQuoteMachine;
